// Weather.js
import React, { useEffect, useState } from "react";
import Search from "../search";
import CurrentWeather from "../CurrentWeather";
import Forecast from "../Forecast";
import { fetchWeatherData, fetchForecastData } from "../api";
import { useGeolocation } from "../hooks/useGeolocation";
import { useLocalStorage } from "../hooks/useLocalStorage";
import { motion, AnimatePresence } from "framer-motion";
import { FaSun, FaMoon, FaMapMarkerAlt } from "react-icons/fa";

export default function Weather() {
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [unit, setUnit] = useLocalStorage("unit", "metric"); // Store unit preference
  const [darkMode, setDarkMode] = useLocalStorage("darkMode", false);
  const { latitude, longitude, error: geoError } = useGeolocation();
  const [lastSearched, setLastSearched] = useLocalStorage("lastSearched", "");

  const [bgImage, setBgImage] = useState("");

  useEffect(() => {
    // Set background image based on time of day
    const hour = new Date().getHours();
    if (hour >= 6 && hour < 20) {
      setBgImage("https://source.unsplash.com/1600x900/?sunny,day");
    } else {
      setBgImage("https://source.unsplash.com/1600x900/?night,stars");
    }
  }, []);
  useEffect(() => {
    if (lastSearched) {
      handleSearch(lastSearched);
    } else if (latitude && longitude) {
      handleGeolocation();
    }
  }, [latitude, longitude]);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
  }, [darkMode]);

  async function handleSearch(searchTerm) {
    setLoading(true);
    setError(null);
    try {
      const weather = await fetchWeatherData(searchTerm, unit);
      setWeatherData(weather);
      const forecast = await fetchForecastData(searchTerm, unit);
      setForecastData(forecast);
      setLastSearched(searchTerm);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleGeolocation() {
    if (latitude && longitude) {
      setLoading(true);
      setError(null);
      try {
        const weather = await fetchWeatherData(`${latitude},${longitude}`, unit);
        setWeatherData(weather);
        const forecast = await fetchForecastData(`${latitude},${longitude}`, unit);
        setForecastData(forecast);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
  }

  function handleUnitToggle() {
    setUnit(prevUnit => prevUnit === "metric" ? "imperial" : "metric");
  }

  useEffect(() => {
    if (weatherData) {
      handleSearch(weatherData.name);
    }
  }, [unit]);

  return (
    <div className={`weather-app ${darkMode ? 'dark' : 'light'}`}>
      <header>
        <h1>Weather App</h1>
        <div className="header-buttons">
          <button className="mode-toggle" onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
          <button className="unit-toggle" onClick={handleUnitToggle}>
            Switch to {unit === "metric" ? "째F" : "째C"}
          </button>
        </div>
      </header>
      <Search
        search={search}
        setSearch={setSearch}
        onSearch={handleSearch}
      />
      <button className="geo-button" onClick={handleGeolocation} disabled={!latitude || !longitude}>
        Use My Location
      </button>
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {geoError && <div className="error">{geoError}</div>}
      {weatherData && !loading && !error && (
        <CurrentWeather data={weatherData} unit={unit} />
      )}
      {forecastData && !loading && !error && (
        <Forecast data={forecastData} unit={unit} />
      )}
    </div>
  );
}
